"use strict";const body=document.body,header=document.querySelector(".header"),menuBurger=document.querySelector(".header__burger"),hmLinks=document.querySelectorAll(".header-nav ul li a");menuBurger&&(menuBurger.onclick=function(){body.classList.toggle("no-scroll"),menuBurger.classList.toggle("open"),header.classList.toggle("mobile-menu-opened")});for(let e of hmLinks)e.addEventListener("click",()=>{body.classList.remove("no-scroll"),menuBurger.classList.remove("open"),header.classList.remove("mobile-menu-opened")});