"use strict";const body=document.body,header=document.querySelector(".header"),menuBurger=document.querySelector(".header__burger"),hmLinks=document.querySelectorAll(".header-nav li a");menuBurger&&(menuBurger.onclick=function(){body.classList.toggle("no-scroll"),menuBurger.classList.toggle("open"),header.classList.toggle("mobile-menu-opened")});for(let e of hmLinks)e.addEventListener("click",()=>{body.classList.remove("no-scroll"),menuBurger.classList.remove("open"),header.classList.remove("mobile-menu-opened")});const upButton=document.querySelector(".up-btn"),faqItems=(window.addEventListener("scroll",()=>{200<window.scrollY?upButton.classList.remove("hide"):upButton.classList.add("hide")}),upButton.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})}),document.querySelectorAll(".faq-item"));faqItems.forEach(e=>{var t=e.querySelector(".faq-item__question");const o=e.querySelector(".faq-item__answer");t.addEventListener("click",()=>{o.style.maxHeight?(o.style.maxHeight=null,e.classList.remove("faq-item--active")):(o.style.maxHeight=o.scrollHeight+"px",e.classList.add("faq-item--active"))})}),document.querySelectorAll('[data-modal="request"]').forEach(e=>{e.addEventListener("click",()=>{var e=document.getElementById("request");e&&e.scrollIntoView({behavior:"smooth"})})});